for(( i=0;i<${#TABLENAMES[@]};i++)) do 
if [ "${TABLENAMES[i]}" = "integration_log_message" ] ; then
  psql -h $HOST -U $USERNAME -d $DBNAME -p $PORT -A -t -c  "\COPY (SELECT ID, COMBO_MESSAGE_ID, VERSION, SUBSTRING(TRIM(BOTH ' ' from REGEXP_REPLACE(REGEXP_REPLACE(MESSAGE, E'[\\n\\r]+', '\n', 'g' ), E'[\\t]+', '\t', 'g')),1, 3500), STAGE, LOG_ID from ${TABLENAMES[i]}) to ${TABLENAMES[i]}${EXTENSION} NULL ''"
elif [ "${TABLENAMES[i]}" = "integration_logs" ] ; then
  psql -h $HOST -U $USERNAME -d $DBNAME -p $PORT -A -t -c  "\COPY (SELECT ID, LOGGED_ON, CORRELATION_ID, SUBSTRING(TRIM(BOTH ' ' from REGEXP_REPLACE(REGEXP_REPLACE(NOTES, E'[\\n\\r]+', '\n', 'g' ), E'[\\t]+', '\t', 'g')),1, 3500), VERSION, GRID_ID, ENTITY, OPERATION, SYNCH_STATUS from ${TABLENAMES[i]}) to ${TABLENAMES[i]}${EXTENSION} NULL ''"
else
  psql -h $HOST -U $USERNAME -d $DBNAME -p $PORT -A -t -c  "\COPY ${TABLENAMES[i]} to ${TABLENAMES[i]}${EXTENSION} NULL ''"
fi
done