// pass: https://oracle.qa.semanticbits.com/caaers/services/RaveIntegrationServices

testCase = testRunner.testCase;
testStep = testCase.getTestStepAt(0)

// get properties
sendBOM = "true".equals(testCase.getPropertyValue("sendBOM"))
socketTimeout = testCase.getPropertyValue("socketTimeout").toInteger() // 10000
endpoint = testStep.getPropertyValue("endpoint");
post = new String(testStep.testRequest.messageExchange.rawRequestData)
xml = testStep.testRequest.messageExchange.requestContent

url = new URL(endpoint)
host = url.getHost()
port = url.getPort()
isSSL = url.getProtocol() == "https"
charset = java.nio.charset.StandardCharsets.UTF_8

if (port <= 0) {
	if (isSSL) port = 443
	else port = 80
}

// build soap payload
xmlBytes = xml.getBytes(charset)
bomBytes = new String("\uFEFF").getBytes()
if (! sendBOM) bomBytes = new byte[0]
message = new byte[xmlBytes.length + bomBytes.length]
System.arraycopy(bomBytes, 0, message, 0, bomBytes.length);
System.arraycopy(xmlBytes, 0, message, bomBytes.length, xmlBytes.length);

// modify the content-length to include bom
//If we change Content-Length request will fail. So we commented below block
//if (sendBOM) {
//	post = post.replaceFirst(/Content-Length\: (\d+)/) { all, length -> 
//		"Content-Length: " + message.length
//	}
//}


// pront out some debug info
log.info "BOM Host: " + host
log.info "BOM Port: " + port
log.info "BOM Post: " + post
log.info "BOM XML: " + xml
log.info "BOM Message length: " + message.length

sock = new Socket(url.getHost(), port)

// wrap with an ssl socket if we are using ssl
if (isSSL) {
	sock = javax.net.ssl.SSLSocketFactory.getDefault().createSocket(sock, host, port, true)
}

// set a socket timeout for poorly bahaving servers
if (socketTimeout) {
	sock.setSoTimeout(socketTimeout)
	log.info "BOM Timeout: " + socketTimeout
}

// if you need to debug the payload, uncomment the two lines below and change the file path
//new File("/Users/mccon012",'message.xml').withOutputStream { out ->
//	out.write(message)
//}

// send the http details then the soap payload
// read the response
resp = ""
try {
	os = sock.getOutputStream()
	os.write(post.getBytes(charset))
	os.write(message)
	os.flush()

	is = sock.getInputStream()	
	byte[] buf = new byte[1024]
	int count = 0
	while ((count = is.read(buf, 0, buf.length)) != -1) {
		resp += new String(buf, 0, count)
	}
	os.close()
	is.close()
} catch (SocketTimeoutException e) {
	log.warn "BOM socket timed out but will check response anyway"
} finally {
	sock.close()
}

// print debug message
log.info "BOM Response: " + resp

// assert that we get 200 response code
assert resp.startsWith("HTTP/1.1 200 OK") == true : "BOM failed - didn't return  200"
log.info("Response: ${resp}")
// assert that the payload contains some real stuff
assert resp.contains("saveAndEvaluateAEsResponse") == true : "BOM failed - couldn't find saveAndEvaluateAEs in response"

