con:config.script:
import com.jcraft.jsch.Channel
import com.jcraft.jsch.ChannelSftp
import com.jcraft.jsch.JSch
import com.jcraft.jsch.JSchException
import com.jcraft.jsch.Session
import com.jcraft.jsch.SftpATTRS
import com.jcraft.jsch.SftpException
import org.apache.commons.io.FileUtils

//download the file from done folder 
String e2bFileName = testRunner.testCase.testSteps["course-properties"].getPropertyValue("e2bInputFileName")
String e2bInPath = testRunner.testCase.testSteps["course-properties"].getPropertyValue("e2bInFolder")
String e2bDonePath = "${e2bInPath}/inprogress/done"

//now download all the file created after done file 
String e2bOutPath = testRunner.testCase.testSteps["course-properties"].getPropertyValue("e2bOutFolder")
String server = testRunner.testCase.testSteps["course-properties"].getPropertyValue("server")
String username = testRunner.testCase.testSteps["course-properties"].getPropertyValue("username")
String password = testRunner.testCase.testSteps["course-properties"].getPropertyValue("password")
int port = Integer.parseInt(testRunner.testCase.testSteps["course-properties"].getPropertyValue("port"))
String reportId = testRunner.testCase.testSteps["course-properties"].getPropertyValue("reportId")
String messageNumber = testRunner.testCase.testSteps["course-properties"].getPropertyValue("messageNumber")

JSch jsch = new JSch()
Session session = null
SftpATTRS attrs
try {

   session = jsch.getSession(username, server, port)
   session.setConfig("StrictHostKeyChecking", "no")
   session.setPassword(password)
   session.connect()

   Channel channel = session.openChannel("sftp")
   channel.connect();
   ChannelSftp sftpChannel = (ChannelSftp) channel
  // String currentDirectory = sftpChannel.pwd()
  // println("current directory : ${currentDirectory}")
   sftpChannel.cd("${e2bDonePath}/")
   log.info("current directory : ${sftpChannel.pwd()}")
   try {
   	attrs = sftpChannel.lstat(e2bFileName)
   }
   catch(Exception e) {
   	  log.error("e2b file ${e2bFileName} not found in done folder")
   	  testRunner.fail("e2b file ${e2bFileName} not found in done folder")
   }
   int lastUploadedModifiedDate = attrs.getMTime()
   log.info("lastUploaded Mtime : ${attrs.getMTime()}")

   //Create temp folder in local directory
   File tempDirectory = new File("${System.getProperty('user.home')}/temp")
   if(!tempDirectory.exists()){
       tempDirectory.mkdir()
   }
   //Cleanup temp directory before copying files
   FileUtils.cleanDirectory(tempDirectory)

   sftpChannel.cd("${e2bOutPath}/")
   log.info("current directory : ${sftpChannel.pwd()}")       
   Vector<ChannelSftp.LsEntry> entries = sftpChannel.ls("*").findAll { it.getAttrs().getMTime() > lastUploadedModifiedDate || it.getAttrs().getMTime() == lastUploadedModifiedDate } 
   log.info("entries : ${entries.size()}");
   for (ChannelSftp.LsEntry entry : entries) {      
           //get output stream
           OutputStream output = new FileOutputStream("${tempDirectory.getPath()}/${entry.getFilename()}");
           sftpChannel.get(entry.getFilename(), output)     
   }

   sftpChannel.exit()
   boolean status = false
   //look for success or failures in the file.
   log.info("find .act file which has messageNumber ${messageNumber} ")
   File ackFile = tempDirectory.listFiles().find { it.text.contains("<icsrmessagenumb>${messageNumber}</icsrmessagenumb>") && it.text.contains("<reportacknowledgmentcode>01</reportacknowledgmentcode>") }
   log.info "ack file found: ${ackFile}"
   if(ackFile) {
   	 log.info("The e2b ${e2bFileName} submitted successfully ")		
   }
   else {
   	  log.error("e2b ${e2bFileName} submission failed")
   	  testRunner.fail("e2b ${e2bFileName} submission failed")
   }  

} catch (JSchException e) {
   e.printStackTrace();
} catch (SftpException e) {
   e.printStackTrace();
}
finally {
   session.disconnect();
}

// ${#TestCase#ColHeader}
testCase = testRunner.testCase;

line = context.fileReader.readLine()
log.info "Read line: " + line

if (line != null) {
	String[] props = line.split(",")

	for (int i = 0; i < props.length && i < context.headers.length; i++) {
		log.info "Found property: " + context.headers[i] + "=" + props[i]
		testCase.setPropertyValue(context.headers[i],props[i])
	}

	// loop
	testRunner.gotoStep(1)
}

