new ExpeditedAdverseEventValidator() {
				public boolean validate(ExpeditedAdverseEventReport aeReport,ValidationErrors errors) {
				
				if(!aeReport.isAttributionRequired()) return SUCCESS;
				
				int i = 0;
				for(AdverseEvent ae : aeReport.getAdverseEvents()){
					i++;
					if(!(ae.getAdverseEventTerm() instanceof AdverseEventCtcTerm)) continue;
					if(ae.getGrade().equals(Grade.DEATH)) continue;
					if(!ae.isAttributedWith(new Attribution[]{Attribution.POSSIBLE, Attribution.PROBABLE, Attribution.DEFINITE})){
						errors.addValidationError("AER_BR7_ERR", 
								"At least one attribution with one of the following values should present for every AE entered on the report i.e. 'Possible', 'Probable' and 'Definite'. AEs reported on the 'DEATH' category are considered as an exception and are not required to meet this specification",
								i);
					}
				}
				return errors.getErrorCount() > 0 ? FAIL : SUCCESS;
			}
		}.validate(aeReport, errors)

new ExpeditedAdverseEventValidator() {
				public boolean validate(ExpeditedAdverseEventReport aeReport,ValidationErrors errors) {
				
				if(!aeReport.isAttributionRequired()) return SUCCESS;
				
				int i = 0;
				for(AdverseEvent ae : aeReport.getAdverseEvents()){
					i++;
					if( (NullSafeFieldExtractor.extractFieldLength(aeReport,"concomitantMedications") != NullSafeFieldExtractor.extractFieldLength(ae,"concomitantMedicationAttributions"))
					   ||(NullSafeFieldExtractor.extractFieldLength(aeReport,"treatmentInformation.courseAgents") != NullSafeFieldExtractor.extractFieldLength(ae,"courseAgentAttributions"))
					   ||(NullSafeFieldExtractor.extractFieldLength(aeReport,"medicalDevices") != NullSafeFieldExtractor.extractFieldLength(ae,"deviceAttributions"))
					   ||(NullSafeFieldExtractor.extractFieldLength(aeReport,"radiationInterventions") != NullSafeFieldExtractor.extractFieldLength(ae,"radiationAttributions"))
					   ||(NullSafeFieldExtractor.extractFieldLength(aeReport,"surgeryInterventions") != NullSafeFieldExtractor.extractFieldLength(ae,"surgeryAttributions"))
					   ||( ( (NullSafeFieldExtractor.extractFieldLength(aeReport.getStudy(),"ctepStudyDiseases") + NullSafeFieldExtractor.extractFieldLength(aeReport.getStudy(), "meddraStudyDiseases")) >  0) && (1 != NullSafeFieldExtractor.extractFieldLength(ae,"diseaseAttributions")))
					   ||(NullSafeFieldExtractor.extractFieldLength(aeReport,"otherCauses") != NullSafeFieldExtractor.extractFieldLength(ae,"otherCauseAttributions"))){
					   
					   errors.addValidationError("ATT_BR1_ERR","AE must be attributed to all CAUSAL factors on report",i,ae.getAdverseEventTerm().getUniversalTerm());
					}
    				  
				}
				return errors.getErrorCount() > 0 ? FAIL : SUCCESS;
			}
		}.validate(aeReport, errors)
